import React, { Component } from 'react';

class TodoList extends Component {
    constructor() {
        super();
        this.state = {
            userInput: '',
            items: [],
            tache: [],
            date: [],
            etat: []
        };
    }

    onChange(event) {
        this.setState({
            userInput: event.target.value
        });
    }

    addTodo(event) {
        event.preventDefault();
        this.setState({
            userInput: '',
            items: [...this.state.tache,this.state.date,this.state.etat, this.state.userInput]
        });
    }

    deleteTodo(item) {
        // no event 
        // pass the item to indexOf
        const array = this.state.items;
        const index = array.indexOf(item);
        array.splice(index, 1);
        this.setState({
            items: array
        });
    }

    // add item to deleteTodo.bind(this, item)
    renderTodos() {
        return this.state.items.map((item) => {
            return (
                <div className="list-group-item" key={item}>
                    {item} | <button onClick={this.deleteTodo.bind(this, item)}>X</button>
                </div>    
            );
        });
    }

    render() {
        return(
            <div>
                <h1 align="center">TodoList</h1>
                <form className="form-row align-items-center">
                    <label> Nom de tâche : </label>
                    <input value={this.state.userInput} type="text" placeholder="Renseigner un item" onChange={this.onChange.bind(this)} 
                    className="form-control mb-2"
                    />
                    <label> Date de tâche : </label>
                    <input type="date" min="2021-01-01" value={this.state.userInput} onChange={this.onChange.bind(this)}/>
                    <button onClick={this.addTodo.bind(this)} className="btn btn-primary"> Ajouter </button>
                </form>
                <div className="list-group">
                    {this.renderTodos()}
                </div>
            </div>
        );
    }
}

export default TodoList;
