import React, { useState } from 'react';
import { Component } from 'react/cjs/react.production.min';
import './Header.css';

class Todo extends Component{
    constructor(){
        super();
        this.settache = {
            userInput: '',
            tache: []
        }
    };

    onChange(event) {
        this.setState({
            userInput: event.target.value
        });
    }

    ajouter(event){
        console.log()
    }

    render(){
        return(
            <div class="entete">
                <label> Nom de tâche : </label>
                <input value={this.settache.userInput} type="text" placeholder='Mettre une tâche' id="value1" onChange={this.onChange.bind(this)}/>
                <label> Date de tâche : </label>
                <input type="date" id="value2" min="2021-01-01"  />
                <label> Etat : </label>
                <select id="value3">
                    <option value="">--état de la tâche--</option>
                    <option value="a_faire">A faire</option>
                    <option value="commencer">Tout juste commencée</option>
                    <option value="en_cours">En cours</option>
                    <option value="fini">Fini</option>
                </select>
                <input type="submit" value="Ajouter" onClick={this.ajouter.bind(this)}/>
            </div>
        );
    }
}

export default Todo;
