import React, { Component, useState } from 'react';

class TodoList extends Component {
    constructor() {
        super();
        this.state = {
            items: [],
            tache: '',
            date: '',
            etat: ''
        };
    }
    onChangeTache(event) {
        this.setState({
            tache: event.target.value,
        });
    }

    onChangeDate(event){
        this.setState({
            date: event.target.value
        });
    }

    onChangeEtat(event){
        this.setState({
            etat: event.target.value
        });
    }

    ajouter(event) {
        event.preventDefault();
        if(this.state.tache != '' && this.state.date != '' && this.state.date != ''){
            this.setState({
                items: [{"tache" : this.state.tache,"date" : this.state.date,"etat": this.state.etat}],
            });
            todos.push(this.state.items);
            return todos;
        }
        else alert("Veuillez remplir tous les champs");
    }

    supprimer(item) {
        // no event 
        // pass the item to indexOf
        const array = this.state.items;
        const index = array.indexOf(item);
        array.splice(index, 1);
        this.setState({
            items: array
        });
    }

    modifier(item){

    }
    // add item to deleteTodo.bind(this, item)
    renderTodos() {
        return this.todos.map((item) => {
            return (
                <div className="list-group-item" key={item}>
                    {item.tache} | {item.date} | {item.etat} <button onClick={this.supprimer.bind(this, item)}>Delete</button> 
                    <button onClick={this.modifier.bind(this, item)}>Modifier</button>
                </div>    
            );
        });
    }

    render() {
        return(
            <div>
                <h1 align="center">TodoList</h1>
                <form className="form-row align-items-center">
                    <label> Nom de tâche : </label>
                    <input value={this.state.tache} type="text" placeholder="Renseigner un item" onChange={this.onChangeTache.bind(this)} className="form-control mb-2"/>
                    <label> Date de tâche : </label>
                    <input type="date" min="2021-01-01" value={this.state.date} onChange={this.onChangeDate.bind(this)} className="form-control mb-2"/>
                    <select id="value3" value={this.state.etat} onChange={this.onChangeEtat.bind(this)} className="form-control mb-2">
                        <option value="">--état de la tâche--</option>
                        <option value="A faire">A faire</option>
                        <option value="Commencer">Tout juste commencée</option>
                        <option value="En Cours">En cours</option>
                        <option value="Terminer">Terminer</option>
                    </select>
                    <button onClick={this.ajouter.bind(this)} className="btn btn-primary"> Ajouter </button>
                </form>
                <div className="list-group">
                    {this.renderTodos()}
                </div>
            </div>
        );
    }
}

export default TodoList;
