import React, { useState } from 'react';
import './Header.css';

const Header = function (props){
    const [tache, settache] = useState('');
    const [date, setdate] = useState({varOne:new Date()});
    const [etat, setetat] = useState([{ text: 'état' }]);


    function ajouter(){
       return(<p>{date}</p>) 
    }

    return (
        <div class="entete">
	        <label> Nom de tâche : </label>
            <input type="text" id="value1" value={props.settache}/>
	        <label> Date de tâche : </label>
            <input type="date" id="value2" min="2021-01-01" value={props.setdate} />
            <label> Etat : </label>
            <select id="value3">
                <option value="">--état de la tâche--</option>
                <option value="a_faire">A faire</option>
                <option value="commencer">Tout juste commencée</option>
                <option value="en_cours">En cours</option>
                <option value="fini">Fini</option>
            </select>
            <input type="submit" value="Ajouter" onClick={() => ajouter()}/>
        </div>

    )
}

export default Header;
