import React, { useState } from 'react';
import { Component } from 'react/cjs/react.production.min';
import './Header.css';

class Header extends Component{
    constructor(){
        super();
        this.settache = {
            userInput: '',
            items: []
        }
    };

    ajouter(event){
        console.log()
    }

    render(){
        <div class="entete">
	        <label> Nom de tâche : </label>
            <input type="text" id="value1" value={props.settache.userInput}/>
	        <label> Date de tâche : </label>
            <input type="date" id="value2" min="2021-01-01"  />
            <label> Etat : </label>
            <select id="value3">
                <option value="">--état de la tâche--</option>
                <option value="a_faire">A faire</option>
                <option value="commencer">Tout juste commencée</option>
                <option value="en_cours">En cours</option>
                <option value="fini">Fini</option>
            </select>
            <input type="submit" value="Ajouter" onClick={() => ajouter()}/>
        </div>

    }
}

export default Header;
